import { useState } from "react";
import "./Page3.css";
import Page3pop from "./Page3pop";

function Page3() {
  const [popUp, setPop] = useState(false);
  const openPop = () => {
    setPop(!popUp);
  };

  return (
    <div className="app">
      <div className="bg3">
        <span>
          <button className="buttonPlayer1" onClick={openPop}>
            PLAYER 1{popUp}
          </button>
          <button className="buttonPlayer2">PLAYER 2</button>
        </span>
        <div className="text">PLAYER1</div>
      </div>
      <button className="buttonPlayer2">PLAYER 2</button>
    </div>
  );
}

export default Page3;
