import "./Page3pop.css";

var button = document.getElementById("mainButton");

var openForm = function () {
  button.className = "active";
};

var checkInput = function (input) {
  if (input.value.length > 0) {
    input.className = "active";
  } else {
    input.className = "";
  }
};

var closeForm = function () {
  button.className = "";
};

document.addEventListener("keyup", function (e) {
  if (e.keyCode == 27 || e.keyCode == 13) {
    closeForm();
  }
});

function Page3pop() {
  return (
    <>
      <link
        href="https://fonts.googleapis.com/css?family=Raleway:400,500,300"
        rel="stylesheet"
        type="text/css"
      />
      <div id="mainButton">
        <div className="btn-text" onclick="openForm()">
          Sign In
        </div>
        <div className="modal">
          <div className="close-button" onclick="closeForm()">
            x
          </div>
          <div className="form-title">Sign In</div>
          <div className="input-group">
            <input type="text" id="name" onblur="checkInput(this)" />
            <label htmlFor="name">Username</label>
          </div>
          <div className="input-group">
            <input type="password" id="password" onblur="checkInput(this)" />
            <label htmlFor="password">Password</label>
          </div>
          <div className="form-button" onclick="closeForm()">
            Go
          </div>
          <div className="codepen-by">CodePen by Cole Waldrip</div>
        </div>
      </div>
      <div className="codepen-by">CodePen by Cole Waldrip</div>
    </>
  );
}

export default Page3pop;
