import "./Page3.css";
import React, { useState } from "react";

function Page3() {
  const [popup, setPop] = useState(false);
  const handleClickOpen = () => {
    setPop(!popup);
  };
  const closePopup = () => {
    setPop(false);
  };

  const [name1, setName1] = useState("");

  return (
    <div className="app">
      <div className="bg3">
        <span>
          <button className="buttonPlayer1" onClick={handleClickOpen}>
            PLAYER 1
          </button>
        </span>
        <span>
          <button className="buttonPlayer2">PLAYER 2</button>
        </span>

        <div>
          {popup ? (
            <div className="main">
              <div className="popup">
                <div className="popup-header">
                  <h1 className="fancy">PLAYER1</h1>
                  <h1 onClick={closePopup}>X</h1>
                </div>
                <div>
                  <h5 className="text2">PLEASE TYPE YOUR NAME</h5>

                  <div className="form__group field">
                    <input
                      type="input"
                      className="form__field"
                      placeholder="Name"
                      name="name"
                      id="name"
                      required=""
                      onChange={(e) =>
                        setName1("✅" + e.target.value + " " + "has join")
                      }
                    />
                    <label htmlFor="name" className="form__label">
                      Name
                    </label>
                  </div>
                </div>
                <button className="button-78" onClick={closePopup}>
                  JOIN
                </button>
              </div>
            </div>
          ) : (
            ""
          )}
        </div>

        <div className="text1">{name1} </div>
      </div>
    </div>
  );
}

export default Page3;
