import "./Page3.css";
import React, { useState } from "react";

function Page3() {
  const [popup, setPop] = useState(false);
  const handleClickOpen = () => {
    setPop(!popup);
  };
  const closePopup = () => {
    setPop(false);
  };

  const [name, setName] = useState("");

  return (
    <div className="app">
      <div className="bg3">
        <span>
          <button className="buttonPlayer1" onClick={handleClickOpen}>
            PLAYER 1
          </button>
        </span>
        <span>
          <button className="buttonPlayer2">PLAYER 2</button>
        </span>

        <div>
          {popup ? (
            <div className="main">
              <div className="popup">
                <div className="popup-header">
                  <h1>popup</h1>
                  <h1 onClick={closePopup}>X</h1>
                </div>
                <div>
                  <p>This is simple popup in React js</p>
                  <input
                    onChange={(e) => setName(e.target.value + " " + "has join")}
                  />
                  <button>JOIN</button>
                </div>
              </div>
            </div>
          ) : (
            ""
          )}
        </div>

        <div className="text">{name} </div>
      </div>
    </div>
  );
}

export default Page3;
