import "./Page3.css";
import React, { useState } from "react";

function Page3() {
  const [popup, setPop] = useState(false);
  const handleClickOpen = () => {
    setPop(!popup);
  };
  const closePopup = () => {
    setPop(false);
  };

  const [name, setName] = useState("");

  return (
    <div className="app">
      <div className="bg3">
        <span>
          <button className="buttonPlayer1" onClick={handleClickOpen}>
            PLAYER 1
          </button>
        </span>
        <span>
          <button className="buttonPlayer2">PLAYER 2</button>
        </span>

        <div>
          {popup ? (
            <div className="main">
              <div className="popup">
                <div className="popup-header">
                  <h1>popup</h1>
                  <h1 onClick={closePopup}>X</h1>
                </div>
                <div>
                  <p>This is simple popup in React js</p>
                  <input
                    onChange={(e) => setName(e.target.value + " " + "has join")}
                  />
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="1em"
                    height="1em"
                    viewBox="0 0 24 24"
                  >
                    <path
                      fill="currentColor"
                      d="m21.1 12.5l1.4 1.41l-6.53 6.59L12.5 17l1.4-1.41l2.07 2.08l5.13-5.17M11 4a4 4 0 0 1 4 4a4 4 0 0 1-4 4a4 4 0 0 1-4-4a4 4 0 0 1 4-4m0 2a2 2 0 0 0-2 2a2 2 0 0 0 2 2a2 2 0 0 0 2-2a2 2 0 0 0-2-2m0 7c.68 0 1.5.09 2.41.26l-1.67 1.67l-.74-.03c-2.97 0-6.1 1.46-6.1 2.1v1.1h6.2L13 20H3v-3c0-2.66 5.33-4 8-4Z"
                    ></path>
                  </svg>
                  <button>JOIN</button>
                </div>
              </div>
            </div>
          ) : (
            ""
          )}
        </div>

        <div className="text">{name} </div>
      </div>
    </div>
  );
}

export default Page3;
