import React, { useState } from "react";

function HexagonGrid() {
  const [hexSize, setHexSize] = useState(50);
  const [numHexagons, setNumHexagons] = useState(10);

  function generateHexagons() {
    const hexagons = [];
    for (let i = 0; i < numHexagons; i++) {
      for (let j = 0; j < numHexagons; j++) {
        const x = j * hexSize * 1.5;
        const y =
          i * hexSize * Math.sqrt(3) + ((j % 2) * hexSize * Math.sqrt(3)) / 2;
        const hexPath = generateHexagonPath(x, y, hexSize);
        hexagons.push(
          <path key={`${i}-${j}`} className="hexagon" d={hexPath} />
        );
      }
    }
    return hexagons;
  }

  return (
    <svg
      viewBox={`0 0 ${numHexagons * hexSize * 1.5} ${
        numHexagons * hexSize * Math.sqrt(3)
      }`}
    >
      {generateHexagons()}
    </svg>
  );
}

export default HexagonGrid;
