import "./Page3.css";

function Page3() {
  const [popup, setPop] = useState(false);
  const handleClickOpen = () => {
    setPop(!popup);
  };
  const closePopup = () => {
    setPop(false);
  };

  return (
    <div className="app">
      <div className="bg3">
        <span>
          <button className="buttonPlayer1" onClick={handleClickOpen}>
            PLAYER 1
          </button>
        </span>
        <div>
          {popup ? (
            <div className="main">
              <div className="popup">
                <div className="popup-header">
                  <h1>popup</h1>
                  <h1 onClick={closePopup}>X</h1>
                </div>
                <div>
                  <p>This is simple popup in React js</p>
                </div>
              </div>
            </div>
          ) : (
            ""
          )}
        </div>
        <span>
          <button className="buttonPlayer2">PLAYER 2</button>
        </span>

        <div className="text">PLAYER1</div>
      </div>
    </div>
  );
}

export default Page3;
